function addDefaultLayers() {
    var map=ClearBlade.appSettings.config.mapObj;
    ClearBlade.appSettings.config.layersList=[];
    for (var i=0; i<ClearBlade.appSettings.config.layers.length; i++)
    {
        l=ClearBlade.appSettings.config.layers[i];
        ClearBlade.appSettings.config.layersList.push(l.id)
        map.addLayer(l);
    }
