//JavaScript
DSNAME="firmware"
  dsFileData = datasources[DSNAME]; //Datasource name
  var reader;
  var progress = document.querySelector('.percent');
  function abortRead() {
      reader.abort();
  }

  function errorHandler(evt) {
      switch (evt.target.error.code) {
          case evt.target.error.NOT_FOUND_ERR:
              alert('File Not Found!');
              break;
          case evt.target.error.NOT_READABLE_ERR:
              alert('File is not readable');
              break;
          case evt.target.error.ABORT_ERR:
              break; // noop
          default:
              alert('An error occurred reading this file.');
      };
  }

  function updateProgress(evt) {
      // evt is an ProgressEvent.
      if (evt.lengthComputable) {
          var percentLoaded = Math.round((evt.loaded / evt.total) * 100);
          // Increase the progress bar length.
          if (percentLoaded < 100) {
              progress.style.width = percentLoaded + '%';
              progress.textContent = percentLoaded + '%';
          }
      }
  }

  function fileselected(e) {
      document.getElementById("version").innerHTML = "";
      document.getElementById("filename").value = e.target.value.split("\\")[e.target.value.split("\\").length - 1];
      document.getElementById("fileSize").innerHTML = Math.round(e.target.files[0].size/1024) + " kb";
      document.getElementById("fileType").innerHTML = e.target.files[0].type;
      validateForm();
  }

  function validateForm() {
      if ((document.getElementById("files").value) &&
          (document.getElementById("filename").value) &&
          (document.getElementById("version").value))
          document.getElementById("submit").disabled = false;
      else
          document.getElementById("submit").disabled = true;
  }

  function handleSubmit(evt) {
      // Reset progress indicator on new file selection.
      progress.style.width = '0%';
      progress.textContent = '0%';

      reader = new FileReader();
      reader.onerror = errorHandler;
      reader.onprogress = updateProgress;
      reader.onabort = function(e) {
          alert('File read cancelled');
      };
      reader.onloadstart = function(e) {
          document.getElementById('progress_bar').className = 'loading';
      };
      reader.onload = function(e) {
          document.getElementById("submit").disabled = true;
          // Ensure that the progress bar displays 100% at the end.
          progress.style.width = '100%';
          progress.textContent = '100%';
          var pl = {};
          pl.CREATE=true;
          pl.filename = document.getElementById("filename").value;
          pl.fileversion = document.getElementById("version").value;
          pl.description = document.getElementById("description").value;
          pl.os = document.getElementById("os").value;
          pl.architecture = document.getElementById("arch").value;
          pl.filecontent = this.result;
          datasources[DSNAME].sendData(pl);
          document.getElementById('results').hidden = false;
          var rs = datasources[DSNAME].latestData();
          if (rs.success) {
              document.getElementById("results").innerHTML = "Save Complete";
          }
          else {
              document.getElementById("results").innerHTML = rs.results;
              //console.log(rs.logs);
          }
          //datasources[dsUpdateOnSubmit].refresh();
          setTimeout("document.getElementById('results').hidden=true;$('#refreshTablexx button').click()", 2000);
          setTimeout("document.getElementById('progress_bar').className='';", 2000);
      }
      // Read in the image file as a binary string.
      reader.readAsDataURL(document.getElementById("files").files[0]);
  }

  document.getElementById('files').addEventListener('change', fileselected, false);
  document.getElementById('filename').addEventListener('change', validateForm, false);
  document.getElementById('version').addEventListener('change', validateForm, false);
  document.getElementById('submit').addEventListener('click', handleSubmit, false);

