if (this.datasource.stats)
if (isObject(this.datasource.stats)) {
    var cpu=parseFloat(this.datasource.stats[Object.keys(this.datasource.stats)[0]]["cpu(%)"]).toFixed(2);
    var ram=parseFloat(this.datasource.stats[Object.keys(this.datasource.stats)[0]]["memUsed(%)"]).toFixed(2);
    var disk=parseFloat(100-this.datasource.stats[Object.keys(this.datasource.stats)[0]]["diskFree(GB)"]/this.datasource.stats[Object.keys(this.datasource.stats)[0]]["totalDisk(GB)"]*100).toFixed(2) + "%";
    var totalram=this.datasource.stats[Object.keys(this.datasource.stats)[0]]["totalMem(GB)"];
    var diskFree=this.datasource.stats[Object.keys(this.datasource.stats)[0]]["diskFree(GB)"];
    var totaldisk=this.datasource.stats[Object.keys(this.datasource.stats)[0]]["totalDisk(GB)"];
    document.getElementById("edge_cpu").innerHTML=cpu + "%";
    document.getElementById("edge_uptime").innerHTML=this.datasource.stats[Object.keys(this.datasource.stats)[0]]["uptime(min)"].replace(".", "s.").split(".")[0].replace("h", "h ").replace("m", "m ");


    document.getElementById("edge_ram").innerHTML=ram + "%";
    document.getElementById("edge_ram_free").innerHTML=(1000*(100-ram)/100*totalram).toFixed(1);
    document.getElementById("edge_ram_used").innerHTML=(1000*(ram)/100*totalram).toFixed(1);
    document.getElementById("edge_ram_total").innerHTML=(1000*totalram).toFixed(1);

    document.getElementById("edge_disk").innerHTML=disk;
    document.getElementById("edge_disk_free").innerHTML=diskFree.toFixed(2);
    document.getElementById("edge_disk_used").innerHTML=(totaldisk-diskFree).toFixed(2);
    document.getElementById("edge_disk_total").innerHTML=totaldisk.toFixed(2);
}


function isObject(obj) {
    for (var key in obj) {
        if (hasOwnProperty.call(obj, key)) return true;
    }
    return false;
}

