//JavaScript
if (!ClearBlade.appSettings)
    ClearBlade.appSettings = {};
//JavaScript
ClearBlade.appSettings.datasource = "application_properties";
datasources[ClearBlade.appSettings.datasource].latestData.subscribe(init);
//Widget Configuration Settings (defaults)
ClearBlade.appSettings.config = {
    datasourceMarkerLocations: "location",
    targetContainer: "map",
    finalzoom: 5.1,
    setcenter: [0, 0],
    mappitch: 0,
    initialcenter: [0, 0],
    initialzoom: 5.1,
    map_showmouselatlng: true,
    minzoom: 3,
    mapboxstyle: "mapbox://styles/mapbox/streets-v9"
};

///******Widget Configuration Settings************************///
//Widget Setup
if (typeof(mapboxgl) === "undefined") {
    console.log("Please ensure mapbox is included as part of the external resources");
    return;
}
//Property configurations
ds = datasources[ClearBlade.appSettings.datasource].latestData().results;
for (var p in ds) {
    if (ds.hasOwnProperty(p)) {
        if (ds[p] === "[")
            ClearBlade.appSettings.config[p] = JSON.parse(ds[p]);
        else
            ClearBlade.appSettings.config[p] = ds[p];
    }
}
if (document.getElementById(ClearBlade.appSettings.config.targetContainer).innerHTML === "")
    init();

//Setup Mapbox Control
function init() {
    if (document.getElementById(ClearBlade.appSettings.config.targetContainer).innerHTML.length > 0)
        return;
    mapboxgl.accessToken = ClearBlade.appSettings.config.mapbox_api_key;
    map = new mapboxgl.Map({
        container: ClearBlade.appSettings.config.targetContainer, // container id
        style: ClearBlade.appSettings.config.mapboxstyle, // stylesheet location
        center: ClearBlade.appSettings.config.initialCenter, // starting position [lng, lat]
        zoom: ClearBlade.appSettings.config.initialzoom, // starting zoom
        minZoom: ClearBlade.appSettings.config.minzoom
    });
    // Add zoom and rotation controls to the map.
    map.addControl(new mapboxgl.NavigationControl());
    ClearBlade.appSettings.config.mapObj=map; //Reference for other controls
    map.on('mousemove', function (e) {
    var features = map.queryRenderedFeatures(e.point);
    var data="";
    var exitRRLayer=false;
    var exitCrossingLayer=false;
    features.forEach(function(f){
    if (f.layer.id==="Rail Lines" && !exitRRLayer) {
        data = data +  "<div>Owner: " + f.properties.RROWNER1 + "</div>" +  
            "<div>State: " + f.properties.STATE + "</div>" + 
            "<div>SubDiv: " + f.properties.SUBDIV + "</div>";
            exitRRLayer=true;
    }

    if (f.layer.id==="Railroad Crossings" && !exitCrossingLayer) {
        //console.log(f.properties);
    data = data +  "<div>Crossing: " + f.properties.RAILROAD + "</div>" + 
        "<div>Div: " + f.properties.RRDIV + "</div>" + 
        "<div>SubDiv: " + f.properties.RRSUBDIV + "</div>" + 
        "<div>MP: " + f.properties.MILEPOST + "</div>";
        exitCrossingLayer=true;
        }
    });
    document.getElementById('features').innerHTML=data;
});
    map.on('load', function() {
        addDefaultLayers();
         //addLayer(ClearBlade.appSettings.config.map);
    map.addSource('contours', {
        type: 'vector',
        url: 'mapbox://mapbox.mapbox-terrain-v2'
    });
       /* map.on('click', function(e) {
            var features = ClearBlade.appSettings.map.queryRenderedFeatures(e.point, { layers: ['wayside_points'] });
            console.log(features);
            if (features) {
                CB_PORTAL.selectPage("/home")
            }
        }); */
    });
}

function addDefaultLayers() {
    var map=ClearBlade.appSettings.config.mapObj;
    ClearBlade.appSettings.config.layersList=[];
    for (var i=0; i<ClearBlade.appSettings.config.layers.length; i++)
    {
        l=ClearBlade.appSettings.config.layers[i];
        ClearBlade.appSettings.config.layersList.push(l.id)
        map.addLayer(l);
    }
    //ClearBlade.appSettings.config.layersList.push("raillines");
    configLayersList(ClearBlade.appSettings.config.layersList.sort());
}

function addLayer(map) {
    var geojson = [];
    ClearBlade.appSettings.datasource.waysides.results.DATA.forEach(function(d) {
        geojson.push({
            "type": "Feature",
            "geometry": {
                "type": "Point",
                "coordinates": [d.lng, d.lat]
            },
            "properties": {
                "icon": d.icon,
                "title": d.name
            }
        });
    });


    var layer = {
        "id": "wayside_points",
        "interactive": true,
        "type": "symbol",
        "source": {
            "type": "geojson",
            "data": {
                "type": "FeatureCollection",
                "features": geojson
            }
        },
        "layout": {
            "icon-image": "point-large",
            "text-field": "{title}",
            "text-font": ["Arial Unicode MS Bold"],
            "text-offset": [0, 0.6],
            "text-anchor": "top"
        },
        "paint": {
            "text-color": "#fff",
            "text-halo-color": "#000",
            "text-halo-width": 4
        }
    };
    map.addLayer(layer);
}
function configLayersList() {
var toggleableLayerIds=ClearBlade.appSettings.config.layersList;
for (var i = 0; i < toggleableLayerIds.length; i++) {
    var id = toggleableLayerIds[i];

    var link = document.createElement('a');
    link.href = '#';
    link.className = 'active';
    link.textContent = id;

    link.onclick = function (e) {
        var clickedLayer = this.textContent;
        e.preventDefault();
        e.stopPropagation();

        var visibility = map.getLayoutProperty(clickedLayer, 'visibility');

        if (visibility === 'visible') {
            map.setLayoutProperty(clickedLayer, 'visibility', 'none');
            this.className = '';
        } else {
            this.className = 'active';
            map.setLayoutProperty(clickedLayer, 'visibility', 'visible');
        }
    };

    var layers = document.getElementById('menu');
    layers.appendChild(link);
}
}

