    if (bc[i]==="1") {
        ts=new Date(dsCoils.timestamp).toLocaleString(); // The 0 there is the key, which sets the date to the epoch
        alarm="Active";
    }
    crossing.push({id: "ci" + i, value: alarm, description: crossingMap[i].name, timestamp: ts });
}

for (var i=0;i<powerMap.length;i++) {
